mapper.update:
UPDATE RESTDE
   SET   RESTDE_NAME = #{restDeName}
   
     
	 
	 
	 , UPDT_EMPNO = #{regEmpNo}
	 , UPDT_DT =  now(6)
 WHERE RESTDE_NO = #{restDeNo}

UPDATE RESTDE
   SET USED_YN = #{usedYn}
	 , UPDT_EMPNO = #{regEmpNo}
	 , UPDT_DT =   now(6)
 WHERE RESTDE_NO = #{restDeNo}

mapper.select:
SELECT R.RESVE_DE , R.STTUS_CODE , R.RESVE_NO , IFNULL(R.RESVE_EMPNO,'') AS RESVE_EMPNO , IFNULL( R.WAIT_EMPNO,'') AS WAIT_EMPNO FROM ( SELECT A.RESVE_NO , A.RESVE_DE , CASE B.STTUS_CODE WHEN 'STS03' THEN 'STS01' ELSE B.STTUS_CODE END AS STTUS_CODE , A.RESVE_EMPNO , A.WAIT_EMPNO FROM RESVE_STTUS A JOIN RESVE_HIST B ON A.RESVE_NO = B.RESVE_NO AND A.RESVE_DE BETWEEN #{startDate} AND #{endDate} AND A.CANCL_YN ='N' AND A.COMPT_YN ='N' AND B.REG_DT = (SELECT MAX(AA.REG_DT) FROM RESVE_HIST AA WHERE AA.RESVE_NO = A.RESVE_NO )
WHERE B.STTUS_CODE < 'STS05'
) R WHERE R.RESVE_DE BETWEEN #{startDate} AND #{endDate} ORDER BY R.STTUS_CODE
