mapper.select:
SELECT A.*, B.LAST_STTUS_CODE, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = A.BLD_CODE) AS BLD_NM, (SELECT NCNM FROM MASSEUR WHERE EMPNO = A.MSSR_EMPNO) AS MSSR_NCNM, (SELECT SEXDSTN FROM MASSEUR WHERE EMPNO = A.MSSR_EMPNO) AS MSSR_SEXDSTN, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = A.BED_CODE AND CODE_TYS = A.BLD_CODE) AS BED_NM, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = LAST_STTUS_CODE) AS STTUS_NM, GROUP_CONCAT(A.RESVE_TM ORDER BY RESVE_TM SEPARATOR ',') AS RESVE_TM_LIST FROM resve_sttus A INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) B ON A.RESVE_NO = B.RESVE_NO WHERE 1=1
AND B.LAST_STTUS_CODE <> 'STS99'
AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND BLD_CODE = #{bldCode}
AND MSSR_EMPNO = #{mssrEmpno}
GROUP BY A.RESVE_DE, A.BLD_CODE, A.MSSR_EMPNO, BED_CODE ORDER BY A.RESVE_DE

SELECT count(*) FROM ( SELECT A.* FROM resve_sttus A INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) B ON A.RESVE_NO = B.RESVE_NO WHERE 1=1
AND B.LAST_STTUS_CODE <> 'STS99'
AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND BLD_CODE = #{bldCode}
AND MSSR_EMPNO = #{mssrEmpno}
GROUP BY A.RESVE_DE, A.BLD_CODE, A.MSSR_EMPNO ORDER BY A.RESVE_DE ) C

   SELECT RESVE_DE AS RESVE_DE 
		 , NCNM AS MSSR_NCNM
		 ,RESVE_TM AS RESVE_TM
		 ,RESVE_NO
	 FROM RESVE_STTUS A JOIN MASSEUR B 
	                      ON A.MSSR_EMPNO =B.EMPNO   
	 WHERE RESVE_DE = #{resveDate}
	   AND  MSSR_EMPNO = #{mssrCode}

	   AND RESVE_TM = #{resveTime}
	   AND CANCL_YN ='N'

mapper.update:
       UPDATE RESVE_STTUS
          SET CANCL_YN = #{canclYn}
            , UPDT_EMPNO = #{regEmpNo}
			 , UPDT_DT =  now(3)
       WHERE RESVE_NO = #{RESVE_NO}
