mapper.select:
SELECT A.RESVE_NO, RESVE_DE, RESVE_TM, BLD_CODE, MSSR_EMPNO, BED_CODE, RESVE_EMPNO, WAIT_EMPNO, REG_EMPNO, B.REG_DT, UPDT_EMPNO, UPDT_DT, LAST_STTUS_CODE, LAST_STTUS_CODE AS LAST_STTUS, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = A.RESVE_TM) AS RESVE_TM_TXT, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = A.BLD_CODE) AS BLD_NM, (SELECT NCNM FROM MASSEUR WHERE EMPNO = A.MSSR_EMPNO) AS MSSR_NCNM, (SELECT SEXDSTN FROM MASSEUR WHERE EMPNO = A.MSSR_EMPNO) AS MSSR_SEXDSTN, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = A.BED_CODE AND CODE_TYS = A.BLD_CODE) AS BED_NM, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = LAST_STTUS_CODE) AS STTUS_NM, DATE_FORMAT(B.REG_DT, '%Y-%m-%d') AS REG_DT_TXT FROM resve_sttus A INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE, T1.REG_DT FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST WHERE TARGET_EMPNO = #{empNo} GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) B ON A.RESVE_NO = B.RESVE_NO WHERE 1=1 AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND LAST_STTUS_CODE = #{statusCode}
ORDER BY A.RESVE_DE DESC,B.REG_DT DESC

SELECT COUNT(*) FROM resve_sttus A INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST WHERE TARGET_EMPNO = #{empNo} GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) B ON A.RESVE_NO = B.RESVE_NO WHERE 1=1 AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND LAST_STTUS_CODE = #{statusCode}
ORDER BY A.RESVE_DE DESC
