mapper.select:
SELECT * FROM ( SELECT STS.*, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = STS.RESVE_TM) AS RESVE_TM_TXT, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = STS.BLD_CODE) AS BLD_NM, (SELECT NCNM FROM MASSEUR WHERE EMPNO = STS.MSSR_EMPNO) AS MSSR_NCNM, (SELECT SEXDSTN FROM MASSEUR WHERE EMPNO = STS.MSSR_EMPNO) AS MSSR_SEXDSTN, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = STS.BED_CODE AND CODE_TYS = STS.BLD_CODE) AS BED_NM, (SELECT HNAME FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.RESVE_EMPNO) AS RESVE_EMPNM, (SELECT DEPTNM FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.RESVE_EMPNO) AS RESVE_DEPTNM, (SELECT HNAME FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.WAIT_EMPNO) AS WAIT_EMPNM, (SELECT DEPTNM FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.WAIT_EMPNO) AS WAIT_DEPTNM, (SELECT DATE_FORMAT(MAX(REG_DT), '%Y-%m-%d %H:%i') FROM RESVE_HIST WHERE RESVE_NO = STS.RESVE_NO AND TARGET_EMPNO = STS.RESVE_EMPNO AND STTUS_CODE='STS01') AS RESVE_DT, (SELECT DATE_FORMAT(MAX(REG_DT), '%Y-%m-%d %H:%i') FROM RESVE_HIST WHERE RESVE_NO = STS.RESVE_NO AND TARGET_EMPNO = STS.WAIT_EMPNO AND STTUS_CODE='STS03') AS WAIT_DT, HIST.LAST_STTUS_CODE, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE_TYL = 'STS' AND CODE = HIST.LAST_STTUS_CODE ) AS LAST_STTUS_NM FROM RESVE_STTUS STS INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) HIST ON STS.RESVE_NO = HIST.RESVE_NO WHERE 1=1 AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND BLD_CODE = #{bldCode}
AND BED_CODE = #{bedCode}
AND LAST_STTUS_CODE = #{statusCode}
AND CANCL_YN != 'Y' AND ((RESVE_EMPNO IS NOT NULL AND RESVE_EMPNO != '') OR (WAIT_EMPNO IS NOT NULL AND WAIT_EMPNO != '')) ) TBL WHERE 1=1
AND ( RESVE_EMPNM LIKE CONCAT('%', #{empnm}, '%') OR WAIT_EMPNM LIKE CONCAT('%', #{empnm}, '%'))

SELECT COUNT(*) FROM ( SELECT STS.*, (SELECT HNAME FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.RESVE_EMPNO) AS RESVE_EMPNM, (SELECT HNAME FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.WAIT_EMPNO) AS WAIT_EMPNM FROM RESVE_STTUS STS INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) HIST ON STS.RESVE_NO = HIST.RESVE_NO WHERE 1=1 AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND BLD_CODE = #{bldCode}
AND BED_CODE = #{bedCode}
AND LAST_STTUS_CODE = #{statusCode}
AND CANCL_YN != 'Y' AND ((RESVE_EMPNO IS NOT NULL AND RESVE_EMPNO != '') OR (WAIT_EMPNO IS NOT NULL AND WAIT_EMPNO != '')) ) TBL WHERE 1=1
AND ( RESVE_EMPNM LIKE CONCAT('%', #{empnm}, '%') OR WAIT_EMPNM LIKE CONCAT('%', #{empnm}, '%'))

SELECT * FROM ( SELECT STS.*, DATE_FORMAT(RESVE_DE, '%Y-%m-%d') AS RESVE_DE_TXT, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = STS.RESVE_TM) AS RESVE_TM_TXT, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = STS.BLD_CODE) AS BLD_NM, (SELECT NCNM FROM MASSEUR WHERE EMPNO = STS.MSSR_EMPNO) AS MSSR_NCNM, (SELECT SEXDSTN FROM MASSEUR WHERE EMPNO = STS.MSSR_EMPNO) AS MSSR_SEXDSTN, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE = STS.BED_CODE AND CODE_TYS = STS.BLD_CODE) AS BED_NM, (SELECT HNAME FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.RESVE_EMPNO) AS RESVE_EMPNM, (SELECT DEPTNM FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.RESVE_EMPNO) AS RESVE_DEPTNM, (SELECT HNAME FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.WAIT_EMPNO) AS WAIT_EMPNM, (SELECT DEPTNM FROM INF_PERSON_INFO_RCV WHERE EMPNO = STS.WAIT_EMPNO) AS WAIT_DEPTNM, (SELECT DATE_FORMAT(MAX(REG_DT), '%Y-%m-%d %H:%i') FROM RESVE_HIST WHERE RESVE_NO = STS.RESVE_NO AND TARGET_EMPNO = STS.RESVE_EMPNO AND STTUS_CODE='STS01') AS RESVE_DT, (SELECT DATE_FORMAT(MAX(REG_DT), '%Y-%m-%d %H:%i') FROM RESVE_HIST WHERE RESVE_NO = STS.RESVE_NO AND TARGET_EMPNO = STS.WAIT_EMPNO AND STTUS_CODE='STS03') AS WAIT_DT, HIST.LAST_STTUS_CODE, (SELECT CODE_NM FROM CMMN_CODE WHERE CODE_TYL = 'STS' AND CODE = HIST.LAST_STTUS_CODE ) AS LAST_STTUS_NM FROM RESVE_STTUS STS INNER JOIN ( SELECT T1.RESVE_NO, T1.STTUS_CODE AS LAST_STTUS_CODE FROM RESVE_HIST T1 INNER JOIN ( SELECT RESVE_NO, MAX(REG_DT) AS REG_DT FROM RESVE_HIST GROUP BY RESVE_NO ) T2 ON T1.RESVE_NO = T2.RESVE_NO AND T1.REG_DT = T2.REG_DT ) HIST ON STS.RESVE_NO = HIST.RESVE_NO WHERE 1=1 AND RESVE_DE BETWEEN #{fromDate} AND #{toDate}
AND BLD_CODE = #{bldCode}
AND BED_CODE = #{bedCode}
AND LAST_STTUS_CODE = #{statusCode}
AND CANCL_YN != 'Y' AND ((RESVE_EMPNO IS NOT NULL AND RESVE_EMPNO != '') OR (WAIT_EMPNO IS NOT NULL AND WAIT_EMPNO != '')) ) TBL WHERE 1=1
AND ( RESVE_EMPNM LIKE CONCAT('%', #{empnm}, '%') OR WAIT_EMPNM LIKE CONCAT('%', #{empnm}, '%'))
ORDER BY RESVE_DE DESC, RESVE_TM DESC , BLD_CODE, BED_CODE
