mapper.select:

SELECT (
	SELECT COUNT(RESVE_NO)
	FROM resve_sttus 		
	WHERE RESVE_DE = #{resveDe}
	AND RESVE_EMPNO = #{empno}
    AND CANCL_YN != 'Y'
) AS RESVE_CNT ,
(
	SELECT COUNT(RESVE_NO)
	FROM resve_sttus	
	WHERE RESVE_DE = #{resveDe}
	AND RESVE_TM >= #{nextResveTm}	
	AND WAIT_EMPNO = #{empno}
	AND CANCL_YN != 'Y'
    AND COMPT_YN != 'Y'
) AS WAIT_CNT
